// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity =0.8.19;

import { LinearERC20Voting } from "./LinearERC20Voting.sol";
import { VotesERC20Wrapper } from "../VotesERC20Wrapper.sol";

 /**
  * An extension of [LinearERC20Voting](./azorius/LinearERC20Voting.md) that properly supports
  * [VotesERC20Wrapper](./VotesERC20Wrapper.md) token governance.
  *
  * This snapshots and uses the total supply of the underlying token for calculating quorum,
  * rather than the total supply of *wrapped* tokens, as would be the case without it.
  */
contract LinearERC20WrappedVoting is LinearERC20Voting {

    /** `proposalId` to "past total supply" of tokens. */
    mapping(uint256 => uint256) internal votingSupply;

    /** @inheritdoc LinearERC20Voting*/
    function initializeProposal(bytes memory _data) public virtual override onlyAzorius {
        uint32 proposalId = abi.decode(_data, (uint32));
        uint32 _votingEndBlock = uint32(block.number) + votingPeriod;

        proposalVotes[proposalId].votingEndBlock = _votingEndBlock;
        proposalVotes[proposalId].votingStartBlock = uint32(block.number);
        votingSupply[proposalId] = VotesERC20Wrapper(address(governanceToken)).underlying().totalSupply();

        emit ProposalInitialized(proposalId, _votingEndBlock);
    }

    /** @inheritdoc LinearERC20Voting*/
    function getProposalVotingSupply(uint32 _proposalId) public view override returns (uint256) {
        return votingSupply[_proposalId];
    }
}

contracts/azorius/LinearERC721Voting.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity =0.8.19;

import { IERC721VotingStrategy } from "./interfaces/IERC721VotingStrategy.sol";
import { IERC721 } from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import { BaseVotingBasisPercent } from "./BaseVotingBasisPercent.sol";
import { IAzorius } from "./interfaces/IAzorius.sol";
import { BaseStrategy } from "./BaseStrategy.sol";

/**
 * An Azorius strategy that allows multiple ERC721 tokens to be registered as governance tokens, 
 * each with their own voting weight.
 *
 * This is slightly different from ERC-20 voting, since there is no way to snapshot ERC721 holdings.
 * Each ERC721 id can vote once, reguardless of what address held it when a proposal was created.
 *
 * Also, this uses "quorumThreshold" rather than LinearERC20Voting's quorumPercent, because the 
 * total supply of NFTs is not knowable within the IERC721 interface.  This is similar to a multisig 
 * "total signers" required, rather than a percentage of the tokens.
 */
contract LinearERC721Voting is BaseStrategy, BaseVotingBasisPercent, IERC721VotingStrategy {

    /**
     * The voting options for a Proposal.
     */
    enum VoteType {
        NO,     // disapproves of executing the Proposal
        YES,    // approves of executing the Proposal
        ABSTAIN // neither YES nor NO, i.e. voting "present"
    }

    /**
     * Defines the current state of votes on a particular Proposal.
     */
    struct ProposalVotes {
        uint32 votingStartBlock; // block that voting starts at
        uint32 votingEndBlock; // block that voting ends
        uint256 noVotes; // current number of NO votes for the Proposal
        uint256 yesVotes; // current number of YES votes for the Proposal
        uint256 abstainVotes; // current number of ABSTAIN votes for the Proposal
        /**
         * ERC-721 contract address to individual NFT id to bool 
         * of whether it has voted on this proposal.
         */
        mapping(address => mapping(uint256 => bool)) hasVoted;
    }

    /** `proposalId` to `ProposalVotes`, the voting state of a Proposal. */
    mapping(uint256 => ProposalVotes) public proposalVotes;

    /** The list of ERC-721 tokens that can vote. */
    address[] public tokenAddresses;

    /** ERC-721 address to its voting weight per NFT id.  */
    mapping(address => uint256) public tokenWeights;

    /** Number of blocks a new Proposal can be voted on. */
    uint32 public votingPeriod;

    /** 
     * The total number of votes required to achieve quorum.
     * "Quorum threshold" is used instead of a quorum percent because IERC721 has no 
     * totalSupply function, so the contract cannot determine this.
     */
    uint256 public quorumThreshold;

    /** 
     * The minimum number of voting power required to create a new proposal.
     */
    uint256 public proposerThreshold; 

    event VotingPeriodUpdated(uint32 votingPeriod);
    event QuorumThresholdUpdated(uint256 quorumThreshold);
    event ProposerThresholdUpdated(uint256 proposerThreshold);
    event ProposalInitialized(uint32 proposalId, uint32 votingEndBlock);
    event Voted(address voter, uint32 proposalId, uint8 voteType, address[] tokenAddresses, uint256[] tokenIds);
    event GovernanceTokenAdded(address token, uint256 weight);
    event GovernanceTokenRemoved(address token);

    error InvalidParams();
    error InvalidProposal();
    error VotingEnded();
    error InvalidVote();
    error InvalidTokenAddress();
    error NoVotingWeight();
    error TokenAlreadySet();
    error TokenNotSet();
    error IdAlreadyVoted(uint256 tokenId);
    error IdNotOwned(uint256 tokenId);

    /**
     * Sets up the contract with its initial parameters.
     *
     * @param initializeParams encoded initialization parameters: `address _owner`,
     * `address[] memory _tokens`, `uint256[] memory _weights`, `address _azoriusModule`, 
     * `uint32 _votingPeriod`, `uint256 _quorumThreshold`, `uint256 _proposerThreshold`, 
     * `uint256 _basisNumerator`
     */
    function setUp(bytes memory initializeParams) public override initializer {
        (
            address _owner,
            address[] memory _tokens,
            uint256[] memory _weights,
            address _azoriusModule,
            uint32 _votingPeriod,
            uint256 _quorumThreshold,
            uint256 _proposerThreshold,
            uint256 _basisNumerator
        ) = abi.decode(
            initializeParams,
            (address, address[], uint256[], address, uint32, uint256, uint256, uint256)
        );

        if (_tokens.length != _weights.length) {
            revert InvalidParams();
        }

        for (uint i = 0; i < _tokens.length;) {
            _addGovernanceToken(_tokens[i], _weights[i]);
            unchecked { ++i; }
        }

        __Ownable_init();
        transferOwnership(_owner);
        _setAzorius(_azoriusModule);
        _updateQuorumThreshold(_quorumThreshold);
        _updateProposerThreshold(_proposerThreshold);
        _updateBasisNumerator(_basisNumerator);
        _updateVotingPeriod(_votingPeriod);

        emit StrategySetUp(_azoriusModule, _owner);
    }

    /**
     * Adds a new ERC-721 token as a governance token, along with its associated weight.
     *
     * @param _tokenAddress the address of the ERC-721 token
     * @param _weight the number of votes each NFT id is worth
     */
    function addGovernanceToken(address _tokenAddress, uint256 _weight) external onlyOwner {
        _addGovernanceToken(_tokenAddress, _weight);
    }

    /**
     * Updates the voting time period for new Proposals.
     *
     * @param _votingPeriod voting time period (in blocks)
     */
    function updateVotingPeriod(uint32 _votingPeriod) external onlyOwner {
        _updateVotingPeriod(_votingPeriod);
    }

    /** 
     * Updates the quorum required for future Proposals.
     *
     * @param _quorumThreshold total voting weight required to achieve quorum
     */
    function updateQuorumThreshold(uint256 _quorumThreshold) external onlyOwner {
        _updateQuorumThreshold(_quorumThreshold);
    }

    /**
     * Updates the voting weight required to submit new Proposals.
     *
     * @param _proposerThreshold required voting weight
     */
    function updateProposerThreshold(uint256 _proposerThreshold) external onlyOwner {
        _updateProposerThreshold(_proposerThreshold);
    }
    /**
     * Returns whole list of governance tokens addresses
     */
    function getAllTokenAddresses() external view returns (address[] memory) {
        return tokenAddresses;
    }

    /**
     * Returns the current state of the specified Proposal.
     *
     * @param _proposalId id of the Proposal
     * @return noVotes current count of "NO" votes
     * @return yesVotes current count of "YES" votes
     * @return abstainVotes current count of "ABSTAIN" votes
     * @return startBlock block number voting starts
     * @return endBlock block number voting ends
     */
    function getProposalVotes(uint32 _proposalId) external view
        returns (
            uint256 noVotes,
            uint256 yesVotes,
            uint256 abstainVotes,
            uint32 startBlock,
            uint32 endBlock
        )
    {
        noVotes = proposalVotes[_proposalId].noVotes;
        yesVotes = proposalVotes[_proposalId].yesVotes;
        abstainVotes = proposalVotes[_proposalId].abstainVotes;
        startBlock = proposalVotes[_proposalId].votingStartBlock;
        endBlock = proposalVotes[_proposalId].votingEndBlock;
    }

    /**
     * Submits a vote on an existing Proposal.
     *
     * @param _proposalId id of the Proposal to vote on
     * @param _voteType Proposal support as defined in VoteType (NO, YES, ABSTAIN)
     * @param _tokenAddresses list of ERC-721 addresses that correspond to ids in _tokenIds
     * @param _tokenIds list of unique token ids that correspond to their ERC-721 address in _tokenAddresses
     */
    function vote(
        uint32 _proposalId, 
        uint8 _voteType, 
        address[] memory _tokenAddresses,
        uint256[] memory _tokenIds 
    ) external {
        if (_tokenAddresses.length != _tokenIds.length) revert InvalidParams();
        _vote(_proposalId, msg.sender, _voteType, _tokenAddresses, _tokenIds);
    }

    /** @inheritdoc IERC721VotingStrategy*/
    function getTokenWeight(address _tokenAddress) external view override returns (uint256) {
        return tokenWeights[_tokenAddress];
    }

    /**
     * Returns whether an NFT id has already voted.
     *
     * @param _proposalId the id of the Proposal
     * @param _tokenAddress the ERC-721 contract address
     * @param _tokenId the unique id of the NFT
     */
    function hasVoted(uint32 _proposalId, address _tokenAddress, uint256 _tokenId) external view returns (bool) {
        return proposalVotes[_proposalId].hasVoted[_tokenAddress][_tokenId];
    }

    /** 
     * Removes the given ERC-721 token address from the list of governance tokens.
     *
     * @param _tokenAddress the ERC-721 token to remove
     */
    function removeGovernanceToken(address _tokenAddress) external onlyOwner {
        if (tokenWeights[_tokenAddress] == 0) revert TokenNotSet();

        tokenWeights[_tokenAddress] = 0;

        uint256 length = tokenAddresses.length;
        for (uint256 i = 0; i < length;) {
            if (_tokenAddress == tokenAddresses[i]) {
                uint256 last = length - 1;
                tokenAddresses[i] = tokenAddresses[last]; // move the last token into the position to remove
                delete tokenAddresses[last];              // delete the last token
                break;
            }
            unchecked { ++i; }
        }

        emit GovernanceTokenRemoved(_tokenAddress);
    }

    /** @inheritdoc BaseStrategy*/
    function initializeProposal(bytes memory _data) public virtual override onlyAzorius {
        uint32 proposalId = abi.decode(_data, (uint32));
        uint32 _votingEndBlock = uint32(block.number) + votingPeriod;

        proposalVotes[proposalId].votingEndBlock = _votingEndBlock;
        proposalVotes[proposalId].votingStartBlock = uint32(block.number);

        emit ProposalInitialized(proposalId, _votingEndBlock);
    }

    /** @inheritdoc BaseStrategy*/
    function isPassed(uint32 _proposalId) public view override returns (bool) {
        return (
            block.number > proposalVotes[_proposalId].votingEndBlock && // voting period has ended
            quorumThreshold <= proposalVotes[_proposalId].yesVotes + proposalVotes[_proposalId].abstainVotes && // yes + abstain votes meets the quorum
            meetsBasis(proposalVotes[_proposalId].yesVotes, proposalVotes[_proposalId].noVotes) // yes votes meets the basis
        );
    }

    /** @inheritdoc BaseStrategy*/
    function isProposer(address _address) public view override returns (bool) {
        uint256 totalWeight = 0;
        for (uint i = 0; i < tokenAddresses.length;) {
            address tokenAddress = tokenAddresses[i];
            totalWeight += IERC721(tokenAddress).balanceOf(_address) * tokenWeights[tokenAddress];
            unchecked { ++i; }
        }
        return totalWeight >= proposerThreshold;
    }

    /** @inheritdoc BaseStrategy*/
    function votingEndBlock(uint32 _proposalId) public view override returns (uint32) {
      return proposalVotes[_proposalId].votingEndBlock;
    }

    /** Internal implementation of `addGovernanceToken` */
    function _addGovernanceToken(address _tokenAddress, uint256 _weight) internal {
        if (!IERC721(_tokenAddress).supportsInterface(0x80ac58cd))
            revert InvalidTokenAddress();

        if (_weight == 0)
            revert NoVotingWeight();

        if (tokenWeights[_tokenAddress] > 0)
            revert TokenAlreadySet();

        tokenAddresses.push(_tokenAddress);
        tokenWeights[_tokenAddress] = _weight;

        emit GovernanceTokenAdded(_tokenAddress, _weight);
    }

    /** Internal implementation of `updateVotingPeriod`. */
    function _updateVotingPeriod(uint32 _votingPeriod) internal {
        votingPeriod = _votingPeriod;
        emit VotingPeriodUpdated(_votingPeriod);
    }

    /** Internal implementation of `updateQuorumThreshold`. */
    function _updateQuorumThreshold(uint256 _quorumThreshold) internal {
        quorumThreshold = _quorumThreshold;
        emit QuorumThresholdUpdated(quorumThreshold);
    }

    /** Internal implementation of `updateProposerThreshold`. */
    function _updateProposerThreshold(uint256 _proposerThreshold) internal {
        proposerThreshold = _proposerThreshold;
        emit ProposerThresholdUpdated(_proposerThreshold);
    }

    /**
     * Internal function for casting a vote on a Proposal.
     *
     * @param _proposalId id of the Proposal
     * @param _voter address casting the vote
     * @param _voteType vote support, as defined in VoteType
     * @param _tokenAddresses list of ERC-721 addresses that correspond to ids in _tokenIds
     * @param _tokenIds list of unique token ids that correspond to their ERC-721 address in _tokenAddresses
     */
    function _vote(
        uint32 _proposalId,
        address _voter,
        uint8 _voteType,
        address[] memory _tokenAddresses,
        uint256[] memory _tokenIds
    ) internal {

        uint256 weight;

        // verifies the voter holds the NFTs and returns the total weight associated with their tokens
        // the frontend will need to determine whether an address can vote on a proposal, as it is possible
        // to vote twice if you get more weight later on
        for (uint256 i = 0; i < _tokenAddresses.length;) {

            address tokenAddress = _tokenAddresses[i];
            uint256 tokenId = _tokenIds[i];

            if (_voter != IERC721(tokenAddress).ownerOf(tokenId)) {
                revert IdNotOwned(tokenId);
            }

            if (proposalVotes[_proposalId].hasVoted[tokenAddress][tokenId] == true) {
                revert IdAlreadyVoted(tokenId);
            }

            weight += tokenWeights[tokenAddress];
            proposalVotes[_proposalId].hasVoted[tokenAddress][tokenId] = true;
            unchecked { ++i; }
        }

        if (weight == 0) revert NoVotingWeight();

        ProposalVotes storage proposal = proposalVotes[_proposalId];

        if (proposal.votingEndBlock == 0)
            revert InvalidProposal();

        if (block.number > proposal.votingEndBlock)
            revert VotingEnded();

        if (_voteType == uint8(VoteType.NO)) {
            proposal.noVotes += weight;
        } else if (_voteType == uint8(VoteType.YES)) {
            proposal.yesVotes += weight;
        } else if (_voteType == uint8(VoteType.ABSTAIN)) {
            proposal.abstainVotes += weight;
        } else {
            revert InvalidVote();
        }

        emit Voted(_voter, _proposalId, _voteType, _tokenAddresses, _tokenIds);
    }
}

contracts/AzoriusFreezeGuard.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity =0.8.19;

import { IBaseFreezeVoting } from "./interfaces/IBaseFreezeVoting.sol";
import { IGuard } from "@gnosis.pm/zodiac/contracts/interfaces/IGuard.sol";
import { FactoryFriendly } from "@gnosis.pm/zodiac/contracts/factory/FactoryFriendly.sol";
import { Enum } from "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";
import { BaseGuard } from "@gnosis.pm/zodiac/contracts/guard/BaseGuard.sol";

/**
 * A Safe Transaction Guard contract that prevents an [Azorius](./azorius/Azorius.md) 
 * subDAO from executing transactions if it has been frozen by its parentDAO.
 *
 * See https://docs.safe.global/learn/safe-core/safe-core-protocol/guards.
 */
contract AzoriusFreezeGuard is FactoryFriendly, IGuard, BaseGuard {

    /**
     * A reference to the freeze voting contract, which manages the freeze
     * voting process and maintains the frozen / unfrozen state of the DAO.
     */
    IBaseFreezeVoting public freezeVoting;

    event AzoriusFreezeGuardSetUp(
        address indexed creator,
        address indexed owner,
        address indexed freezeVoting
    );

    error DAOFrozen();

    constructor() {
      _disableInitializers();
    }

    /**
     * Initialize function, will be triggered when a new instance is deployed.
     *
     * @param initializeParams encoded initialization parameters: `address _owner`,
     * `address _freezeVoting`
     */
    function setUp(bytes memory initializeParams) public override initializer {
        __Ownable_init();
        (address _owner, address _freezeVoting) = abi.decode(
            initializeParams,
            (address, address)
        );

        transferOwnership(_owner);
        freezeVoting = IBaseFreezeVoting(_freezeVoting);

        emit AzoriusFreezeGuardSetUp(msg.sender, _owner, _freezeVoting);
    }

    /**
     * This function is called by the Safe to check if the transaction
     * is able to be executed and reverts if the guard conditions are
     * not met.
     *
     * In our implementation, this reverts if the DAO is frozen.
     */
    function checkTransaction(
        address,
        uint256,
        bytes memory,
        Enum.Operation,
        uint256,
        uint256,
        uint256,
        address,
        address payable,
        bytes memory,
        address
    ) external view override(BaseGuard, IGuard) {
        // if the DAO is currently frozen, revert
        // see BaseFreezeVoting for freeze voting details
        if(freezeVoting.isFrozen()) revert DAOFrozen();
    }

    /**
     * A callback performed after a transaction is executed on the Safe. This is a required
     * function of the `BaseGuard` and `IGuard` interfaces that we do not make use of.
     */
    function checkAfterExecution(bytes32, bool) external view override(BaseGuard, IGuard) {
        // not implementated
    }
}

contracts/BaseFreezeVoting.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity =0.8.19;

import { FactoryFriendly } from "@gnosis.pm/zodiac/contracts/factory/FactoryFriendly.sol";
import { IBaseFreezeVoting } from "./interfaces/IBaseFreezeVoting.sol";

/**
 * The base abstract contract which holds the state of a vote to freeze a childDAO.
 *
 * The freeze feature gives a way for parentDAOs to have a limited measure of control
 * over their created subDAOs.
 *
 * Normally a subDAO operates independently, and can vote on or sign transactions, 
 * however should the parent disagree with a decision made by the subDAO, any parent
 * token holder can initiate a vote to "freeze" it, making executing transactions impossible
 * for the time denoted by `freezePeriod`.
 *
 * This requires a number of votes equal to `freezeVotesThreshold`, within the `freezeProposalPeriod`
 * to be successful.
 *
 * Following a successful freeze vote, the childDAO will be unable to execute transactions, due to
 * a Safe Transaction Guard, until the `freezePeriod` has elapsed.
 */
abstract contract BaseFreezeVoting is FactoryFriendly, IBaseFreezeVoting {

    /** Block number the freeze proposal was created at. */
    uint32 public freezeProposalCreatedBlock;

    /** Number of blocks a freeze proposal has to succeed. */
    uint32 public freezeProposalPeriod;

    /** Number of blocks a freeze lasts, from time of freeze proposal creation. */
    uint32 public freezePeriod;

    /** Number of freeze votes required to activate a freeze. */
    uint256 public freezeVotesThreshold;

    /** Number of accrued freeze votes. */
    uint256 public freezeProposalVoteCount;

    /**
    * Mapping of address to the block the freeze vote was started to 
    * whether the address has voted yet on the freeze proposal.
    */
    mapping(address => mapping(uint256 => bool)) public userHasFreezeVoted;

    event FreezeVoteCast(address indexed voter, uint256 votesCast);
    event FreezeProposalCreated(address indexed creator);
    event FreezeVotesThresholdUpdated(uint256 freezeVotesThreshold);
    event FreezePeriodUpdated(uint32 freezePeriod);
    event FreezeProposalPeriodUpdated(uint32 freezeProposalPeriod);

    constructor() {
      _disableInitializers();
    }

    /**
     * Casts a positive vote to freeze the subDAO. This function is intended to be called
     * by the individual token holders themselves directly, and will allot their token
     * holdings a "yes" votes towards freezing.
     *
     * Additionally, if a vote to freeze is not already running, calling this will initiate
     * a new vote to freeze it.
     */
    function castFreezeVote() external virtual;

    /**
     * Returns true if the DAO is currently frozen, false otherwise.
     * 
     * @return bool whether the DAO is currently frozen
     */
    function isFrozen() external view returns (bool) {
        return freezeProposalVoteCount >= freezeVotesThreshold 
            && block.number < freezeProposalCreatedBlock + freezePeriod;
    }

    /**
     * Unfreezes the DAO, only callable by the owner (parentDAO).
     */
    function unfreeze() external onlyOwner {
        freezeProposalCreatedBlock = 0;
        freezeProposalVoteCount = 0;
    }

    /**
     * Updates the freeze votes threshold, the number of votes required to enact a freeze.
     *
     * @param _freezeVotesThreshold number of freeze votes required to activate a freeze
     */
    function updateFreezeVotesThreshold(uint256 _freezeVotesThreshold) external onlyOwner {
        _updateFreezeVotesThreshold(_freezeVotesThreshold);
    }

    /**
     * Updates the freeze proposal period, the time that parent token holders have to cast votes
     * after a freeze vote has been initiated.
     *
     * @param _freezeProposalPeriod number of blocks a freeze vote has to succeed to enact a freeze
     */
    function updateFreezeProposalPeriod(uint32 _freezeProposalPeriod) external onlyOwner {
        _updateFreezeProposalPeriod(_freezeProposalPeriod);
    }

    /**
     * Updates the freeze period, the time the DAO will be unable to execute transactions for,
     * should a freeze vote pass.
     *
     * @param _freezePeriod number of blocks a freeze lasts, from time of freeze proposal creation
     */
    function updateFreezePeriod(uint32 _freezePeriod) external onlyOwner {
        _updateFreezePeriod(_freezePeriod);
    }

    /** Internal implementation of `updateFreezeVotesThreshold`. */
    function _updateFreezeVotesThreshold(uint256 _freezeVotesThreshold) internal {
        freezeVotesThreshold = _freezeVotesThreshold;
        emit FreezeVotesThresholdUpdated(_freezeVotesThreshold);
    }

    /** Internal implementation of `updateFreezeProposalPeriod`. */
    function _updateFreezeProposalPeriod(uint32 _freezeProposalPeriod) internal {
        freezeProposalPeriod = _freezeProposalPeriod;
        emit FreezeProposalPeriodUpdated(_freezeProposalPeriod);
    }

    /** Internal implementation of `updateFreezePeriod`. */
    function _updateFreezePeriod(uint32 _freezePeriod) internal {
        freezePeriod = _freezePeriod;
        emit FreezePeriodUpdated(_freezePeriod);
    }
}

contracts/DecentHats_0_1_0.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.19;

import {Enum} from "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";
import {IAvatar} from "@gnosis.pm/zodiac/contracts/interfaces/IAvatar.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {IERC6551Registry} from "./interfaces/IERC6551Registry.sol";
import {IHats} from "./interfaces/hats/IHats.sol";

contract DecentHats_0_1_0 {
    string public constant NAME = "DecentHats_0_1_0";

    struct Hat {
        uint32 maxSupply;
        string details;
        string imageURI;
        bool isMutable;
        address wearer;
    }

    struct CreateTreeParams {
        IHats hatsProtocol;
        address hatsAccountImplementation;
        IERC6551Registry registry;
        address keyValuePairs;
        string topHatDetails;
        string topHatImageURI;
        Hat adminHat;
        Hat[] hats;
    }

    function getSalt() internal view returns (bytes32 salt) {
        uint256 chainId;
        assembly {
            chainId := chainid()
        }

        bytes memory concatenatedSaltInput = abi.encodePacked(
            NAME,
            chainId,
            address(this)
        );

        salt = keccak256(concatenatedSaltInput);
    }

    function createTopHat(
        IHats _hatsProtocol,
        string memory _topHatDetails,
        string memory _topHatImageURI
    ) internal returns (uint256) {
        return
            _hatsProtocol.mintTopHat(
                address(this),
                _topHatDetails,
                _topHatImageURI
            );
    }

    function createAccount(
        IERC6551Registry _registry,
        address _hatsAccountImplementation,
        bytes32 salt,
        address protocolAddress,
        uint256 hatId
    ) internal returns (address) {
        return
            _registry.createAccount(
                _hatsAccountImplementation,
                salt,
                block.chainid,
                protocolAddress,
                hatId
            );
    }

    function updateKeyValuePairs(
        address _keyValuePairs,
        uint256 topHatId
    ) internal {
        string[] memory keys = new string[](1);
        string[] memory values = new string[](1);
        keys[0] = "topHatId";
        values[0] = Strings.toString(topHatId);

        IAvatar(msg.sender).execTransactionFromModule(
            _keyValuePairs,
            0,
            abi.encodeWithSignature(
                "updateValues(string[],string[])",
                keys,
                values
            ),
            Enum.Operation.Call
        );
    }

    function createHat(
        IHats _hatsProtocol,
        uint256 adminHatId,
        Hat memory _hat,
        address topHatAccount
    ) internal returns (uint256) {
        return
            _hatsProtocol.createHat(
                adminHatId,
                _hat.details,
                _hat.maxSupply,
                topHatAccount,
                topHatAccount,
                _hat.isMutable,
                _hat.imageURI
            );
    }

    function createHatAccountMint(
        IHats hatsProtocol,
        uint256 adminHatId,
        Hat calldata hat,
        address topHatAccount,
        IERC6551Registry registry,
        address hatsAccountImplementation,
        bytes32 salt
    ) internal returns (uint256 hatId, address accountAddress) {
        hatId = createHat(hatsProtocol, adminHatId, hat, topHatAccount);

        accountAddress = createAccount(
            registry,
            hatsAccountImplementation,
            salt,
            address(hatsProtocol),
            hatId
        );

        if (hat.wearer != address(0)) {
            hatsProtocol.mintHat(hatId, hat.wearer);
        }
    }

    function handleHats(
        IHats _hatsProtocol,
        IERC6551Registry _registry,
        address _hatsAccountImplementation,
        bytes32 salt,
        address topHatAccount,
        uint256 adminHatId,
        Hat[] calldata _hats
    ) internal {
        for (uint256 i = 0; i < _hats.length; ) {
            createHatAccountMint(
                _hatsProtocol,
                adminHatId,
                _hats[i],
                topHatAccount,
                _registry,
                _hatsAccountImplementation,
                salt
            );

            unchecked {
                ++i;
            }
        }
    }

    function createAndDeclareTree(CreateTreeParams calldata params) public {
        bytes32 salt = getSalt();
        uint256 topHatId = createTopHat(
            params.hatsProtocol,
            params.topHatDetails,
            params.topHatImageURI
        );
        address topHatAccount = createAccount(
            params.registry,
            params.hatsAccountImplementation,
            salt,
            address(params.hatsProtocol),
            topHatId
        );

        updateKeyValuePairs(params.keyValuePairs, topHatId);

        (uint256 adminHatId, ) = createHatAccountMint(
            params.hatsProtocol,
            topHatId,
            params.adminHat,
            topHatAccount,
            params.registry,
            params.hatsAccountImplementation,
            salt
        );

        handleHats(
            params.hatsProtocol,
            params.registry,
            params.hatsAccountImplementation,
            salt,
            topHatAccount,
            adminHatId,
            params.hats
        );

        params.hatsProtocol.transferHat(topHatId, address(this), msg.sender);
    }
}

contracts/ERC20Claim.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity =0.8.19;

import {IERC20Claim} from "./interfaces/IERC20Claim.sol";
import {VotesERC20, FactoryFriendly} from "./VotesERC20.sol";
import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * A simple contract that allows for parent DAOs that have created a new ERC-20
 * token voting subDAO to allocate a certain amount of those tokens as claimable
 * by the parent DAO's token holders.
 */
contract ERC20Claim is FactoryFriendly, IERC20Claim {

    using SafeERC20 for IERC20;

    /** The deadline block to claim tokens by, or 0 for indefinite. */
    uint32 public deadlineBlock;

    /** The address of the initial holder of the claimable `childERC20` tokens. */
    address public funder;

    /** Child ERC20 token address, to calculate the percentage claimable. */
    address public childERC20;

    /** Parent ERC20 token address, for calculating a snapshot of holdings. */
    address public parentERC20;

    /** Id of a snapshot of token holdings for this claim (see [VotesERC20](./VotesERC20.md)). */
    uint256 public snapShotId;

    /** Total amount of `childERC20` tokens allocated for claiming by parent holders. */
    uint256 public parentAllocation;

    /** Mapping of address to bool of whether the address has claimed already. */
    mapping(address => bool) public claimed;

    event ERC20ClaimCreated(
        address parentToken,
        address childToken,
        uint256 parentAllocation,
        uint256 snapshotId,
        uint256 deadline
    );

    event ERC20Claimed(
        address indexed pToken,
        address indexed cToken,
        address indexed claimer,
        uint256 amount
    );

    error NoAllocation();
    error AllocationClaimed();
    error NotTheFunder();
    error NoDeadline();
    error DeadlinePending();

    constructor() {
        _disableInitializers();
    }

    /**
     * Initialize function, will be triggered when a new instance is deployed.
     *
     * @param initializeParams encoded initialization parameters: `address _childTokenFunder`,
     * `uint256 _deadlineBlock`, `address _parentERC20`, `address _childERC20`,
     * `uint256 _parentAllocation`
     */
    function setUp(bytes memory initializeParams) public override initializer {
        __Ownable_init();
        (
            uint32 _deadlineBlock,
            address _childTokenFunder,
            address _parentERC20,
            address _childERC20,
            uint256 _parentAllocation
        ) = abi.decode(
                initializeParams,
                (uint32, address, address, address, uint256)
            );

        funder = _childTokenFunder;
        deadlineBlock = _deadlineBlock;
        childERC20 = _childERC20;
        parentERC20 = _parentERC20;
        parentAllocation = _parentAllocation;

        snapShotId = VotesERC20(_parentERC20).captureSnapShot();

        IERC20(_childERC20).safeTransferFrom(
            _childTokenFunder,
            address(this),
            _parentAllocation
        );

        emit ERC20ClaimCreated(
            _parentERC20,
            _childERC20,
            _parentAllocation,
            snapShotId,
            _deadlineBlock
        );
    }

    /** @inheritdoc IERC20Claim*/
    function claimTokens(address claimer) external {
        uint256 amount = getClaimAmount(claimer); // get claimer balance

        if (amount == 0) revert NoAllocation(); // the claimer has not been allocated tokens to claim

        claimed[claimer] = true;

        IERC20(childERC20).safeTransfer(claimer, amount); // transfer claimer balance

        emit ERC20Claimed(parentERC20, childERC20, claimer, amount);
    }

    /** @inheritdoc IERC20Claim*/
    function reclaim() external {
        if (msg.sender != funder) revert NotTheFunder();
        if (deadlineBlock == 0) revert NoDeadline();
        if (block.number < deadlineBlock) revert DeadlinePending();
        IERC20 token = IERC20(childERC20);
        token.safeTransfer(funder, token.balanceOf(address(this)));
    }

    /** @inheritdoc IERC20Claim*/
    function getClaimAmount(address claimer) public view returns (uint256) {
        return
            claimed[claimer]
                ? 0
                : (VotesERC20(parentERC20).balanceOfAt(claimer, snapShotId) *
                    parentAllocation) /
                    VotesERC20(parentERC20).totalSupplyAt(snapShotId);
    }
}

contracts/ERC20FreezeVoting.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity =0.8.19;

import { BaseFreezeVoting, IBaseFreezeVoting } from "./BaseFreezeVoting.sol";
import { Enum } from "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";
import { IVotes } from "@openzeppelin/contracts/governance/utils/IVotes.sol";

/**
 * A [BaseFreezeVoting](./BaseFreezeVoting.md) implementation which handles 
 * freezes on ERC20 based token voting DAOs.
 */
contract ERC20FreezeVoting is BaseFreezeVoting {

    /** A reference to the ERC20 voting token of the subDAO. */
    IVotes public votesERC20;

    event ERC20FreezeVotingSetUp(
        address indexed owner,
        address indexed votesERC20
    );

    error NoVotes();
    error AlreadyVoted();

    /**
     * Initialize function, will be triggered when a new instance is deployed.
     *
     * @param initializeParams encoded initialization parameters: `address _owner`,
     * `uint256 _freezeVotesThreshold`, `uint256 _freezeProposalPeriod`, `uint256 _freezePeriod`,
     * `address _votesERC20`
     */
    function setUp(bytes memory initializeParams) public override initializer {
        (
            address _owner,
            uint256 _freezeVotesThreshold,
            uint32 _freezeProposalPeriod,
            uint32 _freezePeriod,
            address _votesERC20
        ) = abi.decode(
                initializeParams,
                (address, uint256, uint32, uint32, address)
            );

        __Ownable_init();
        _transferOwnership(_owner);
        _updateFreezeVotesThreshold(_freezeVotesThreshold);
        _updateFreezeProposalPeriod(_freezeProposalPeriod);
        _updateFreezePeriod(_freezePeriod);
        freezePeriod = _freezePeriod;
        votesERC20 = IVotes(_votesERC20);

        emit ERC20FreezeVotingSetUp(_owner, _votesERC20);
    }

    /** @inheritdoc BaseFreezeVoting*/
    function castFreezeVote() external override {
        uint256 userVotes;

        if (block.number > freezeProposalCreatedBlock + freezeProposalPeriod) {
            // create a new freeze proposal and set total votes to msg.sender's vote count

            freezeProposalCreatedBlock = uint32(block.number);

            userVotes = votesERC20.getPastVotes(
                msg.sender,
                freezeProposalCreatedBlock - 1
            );

            if (userVotes == 0) revert NoVotes();

            freezeProposalVoteCount = userVotes;

            emit FreezeProposalCreated(msg.sender);
        } else {
            // there is an existing freeze proposal, count user's votes toward it

            if (userHasFreezeVoted[msg.sender][freezeProposalCreatedBlock])
                revert AlreadyVoted();

            userVotes = votesERC20.getPastVotes(
                msg.sender,
                freezeProposalCreatedBlock - 1
            );

            if (userVotes == 0) revert NoVotes();

            freezeProposalVoteCount += userVotes;
        }        

        userHasFreezeVoted[msg.sender][freezeProposalCreatedBlock] = true;

        emit FreezeVoteCast(msg.sender, userVotes);
    }
}

contracts/ERC721FreezeVoting.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity =0.8.19;

import { IERC721VotingStrategy } from "./azorius/interfaces/IERC721VotingStrategy.sol";
import { BaseFreezeVoting, IBaseFreezeVoting } from "./BaseFreezeVoting.sol";
import { IERC721 } from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

/**
 * A [BaseFreezeVoting](./BaseFreezeVoting.md) implementation which handles 
 * freezes on ERC721 based token voting DAOs.
 */
contract ERC721FreezeVoting is BaseFreezeVoting {

    /** A reference to the voting strategy of the parent DAO. */
    IERC721VotingStrategy public strategy;

    /**
     * Mapping of block the freeze vote was started on, to the token address, to token id,
     * to whether that token has been used to vote already.
     */
    mapping(uint256 => mapping(address => mapping(uint256 => bool))) public idHasFreezeVoted;

    event ERC721FreezeVotingSetUp(address indexed owner, address indexed strategy);

    error NoVotes();
    error NotSupported();
    error UnequalArrays();

    /**
     * Initialize function, will be triggered when a new instance is deployed.
     *
     * @param initializeParams encoded initialization parameters.
     */
    function setUp(bytes memory initializeParams) public override initializer {
        (
            address _owner,
            uint256 _freezeVotesThreshold,
            uint32 _freezeProposalPeriod,
            uint32 _freezePeriod,
            address _strategy
        ) = abi.decode(
                initializeParams,
                (address, uint256, uint32, uint32, address)
            );

        __Ownable_init();
        _transferOwnership(_owner);
        _updateFreezeVotesThreshold(_freezeVotesThreshold);
        _updateFreezeProposalPeriod(_freezeProposalPeriod);
        _updateFreezePeriod(_freezePeriod);
        freezePeriod = _freezePeriod;
        strategy = IERC721VotingStrategy(_strategy);

        emit ERC721FreezeVotingSetUp(_owner, _strategy);
    }

    function castFreezeVote() external override pure { revert NotSupported(); }

    function castFreezeVote(address[] memory _tokenAddresses, uint256[] memory _tokenIds) external {
        if (_tokenAddresses.length != _tokenIds.length) revert UnequalArrays();

        if (block.number > freezeProposalCreatedBlock + freezeProposalPeriod) {
            // create a new freeze proposal
            freezeProposalCreatedBlock = uint32(block.number);
            freezeProposalVoteCount = 0;
            emit FreezeProposalCreated(msg.sender);
        }

        uint256 userVotes = _getVotesAndUpdateHasVoted(_tokenAddresses, _tokenIds, msg.sender);
        if (userVotes == 0) revert NoVotes();

        freezeProposalVoteCount += userVotes;     

        emit FreezeVoteCast(msg.sender, userVotes);
    }

    function _getVotesAndUpdateHasVoted(
        address[] memory _tokenAddresses,
        uint256[] memory _tokenIds,
        address _voter
    ) internal returns (uint256) {

        uint256 votes = 0;

        for (uint256 i = 0; i < _tokenAddresses.length; i++) {

            address tokenAddress = _tokenAddresses[i];
            uint256 tokenId = _tokenIds[i];

            if (_voter != IERC721(tokenAddress).ownerOf(tokenId))
                continue;

            if (idHasFreezeVoted[freezeProposalCreatedBlock][tokenAddress][tokenId])
                continue;

            votes += strategy.getTokenWeight(tokenAddress);

            idHasFreezeVoted[freezeProposalCreatedBlock][tokenAddress][tokenId] = true;
        }

        return votes;
    }
}

contracts/FractalModule.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity =0.8.19;

import { Module, Enum } from "@gnosis.pm/zodiac/contracts/core/Module.sol";
import { IFractalModule } from "./interfaces/IFractalModule.sol";

 /**
  * Implementation of [IFractalModule](./interfaces/IFractalModule.md).
  *
  * A Safe module contract that allows for a "parent-child" DAO relationship.
  *
  * Adding the module allows for a designated set of addresses to execute
  * transactions on the Safe, which in our implementation is the set of parent
  * DAOs.
  */
contract FractalModule is IFractalModule, Module {

    /** Mapping of whether an address is a controller (typically a parentDAO). */
    mapping(address => bool) public controllers;

    event ControllersAdded(address[] controllers);
    event ControllersRemoved(address[] controllers);

    error Unauthorized();
    error TxFailed();

    /** Allows only authorized controllers to execute transactions on the Safe. */
    modifier onlyAuthorized() {
        if (owner() != msg.sender && !controllers[msg.sender])
            revert Unauthorized();
        _;
    }

    constructor() {
      _disableInitializers();
    }

    /**
     * Initialize function, will be triggered when a new instance is deployed.
     *
     * @param initializeParams encoded initialization parameters: `address _owner`,
     * `address _avatar`, `address _target`, `address[] memory _controllers`
     */
    function setUp(bytes memory initializeParams) public override initializer {
        __Ownable_init();
        (
            address _owner,                 // controlling DAO
            address _avatar,
            address _target,
            address[] memory _controllers   // authorized controllers
        ) = abi.decode(
                initializeParams,
                (address, address, address, address[])
            );

        setAvatar(_avatar);
        setTarget(_target);
        addControllers(_controllers);
        transferOwnership(_owner);
    }

    /** @inheritdoc IFractalModule*/
    function removeControllers(address[] memory _controllers) external onlyOwner {
        uint256 controllersLength = _controllers.length;
        for (uint256 i; i < controllersLength; ) {
            controllers[_controllers[i]] = false;
            unchecked {
                ++i;
            }
        }
        emit ControllersRemoved(_controllers);
    }

    /** @inheritdoc IFractalModule*/
    function execTx(bytes memory execTxData) public onlyAuthorized {
        (
            address _target,
            uint256 _value,
            bytes memory _data,
            Enum.Operation _operation
        ) = abi.decode(execTxData, (address, uint256, bytes, Enum.Operation));
        if(!exec(_target, _value, _data, _operation)) revert TxFailed();
    }

    /** @inheritdoc IFractalModule*/
    function addControllers(address[] memory _controllers) public onlyOwner {
        uint256 controllersLength = _controllers.length;
        for (uint256 i; i < controllersLength; ) {
            controllers[_controllers[i]] = true;
            unchecked {
                ++i;
            }
        }
        emit ControllersAdded(_controllers);
    }
}

